Get-ADUser -Server **ADSERVER** -SearchBase "OU=domainname,OU=base,DC=domain,DC=loc" -Filter {Enabled -eq $True -and PasswordNeverExpires -eq $False} -Properties "SamAccountName","Name","PasswordNeverExpires","PasswordLastSet","msDS-UserPasswordExpiryTimeComputed" | Select-Object -Property "SamAccountName","Name","PasswordNeverExpires","PasswordLastSet",@{Name="ExpiryDate";Expression={[datetime]::FromFileTime($_."msDS-UserPasswordExpiryTimeComputed")}} | Export-Csv -Path C:\Users\sahmed\Desktop\AD.csv